    await this.getPosts();
  },
  methods: {
    async getPosts() {
      console.log('entrei')
      let data = await axios.get("https://api.valorantes.com.br/streamers/get");
      this.items = data.data.data;
      for(let i = 0; i < this.items.length; i++){
                this.items[i].titleFormated = this.items[i].title.split(" ").join('-');
            }
    }
  }
